meBuffer[5] = '0';  // Add null terminator    CURRENT_TIME = timeBuffer;    // Extract hour    char hourBuffer[3];  // Buffer to hold hour component (HH + null terminator)    strncpy(hourBuffer, datetime + 11, 2);    hourBuffer[2] = '0';  // Add null terminator    CURRENT_HOUR = (int)hourBuffer;    // Extract minute    char minuteBuffer[3];  // Buffer to hold minute component (MM + null terminator)    strncpy(minuteBuffer, datetime + 14, 2);    minuteBuffer[2] = '0';  // Add null terminator    CURRENT_MINUTE = (int)minuteBuffer;    JsonObject dataMeta = doc[\\"meta\\"];    const char* dataMetaTimezone = dataMeta[\\"timezone\\"];    CURRENT_TIMEZONE = dataMetaTimezone;    Serial.println();    Serial.println(CURRENT_DATE);    Serial.println(CURRENT_TIME);    // Close HTTP connection    http.end();  } else {    Serial.print(\\"Error code: \\");    Serial.println(httpResponseCode);  }}void getPrayerTimes() {  WiFiClient client;  HTTPClient http;  // Make request to the URL  String url = \\"http://api.aladhan.com/v1/timings/\\" + CURRENT_DATE + \\"?latitude=\\" + USER_LATITUDE + \\"&longitude=\\" + USER_LONGITUDE + \\"&method=1\\";  http.begin(client, url);  // Send the GET request  int httpResponseCode = http.GET();  if (httpResponseCode > 0) {    String response = http.getString();    DynamicJsonDocument doc(3072);    DeserializationError error = deserializeJson(doc, response);    if (error) {      Serial.print(F(\\"deserializeJson() failed: \\"));      Serial.println(error.f_str());      return;    }    int code = doc[\\"code\\"];    const char* status = doc[\\"status\\"];    JsonObject data = doc[\\"data\\"];    JsonObject dataTimings = data[\\"timings\\"];    //dataTimingsFajr = dataTimings[\\"Fajr\\"];    dataTimingsSunrise = dataTimings[\\"Sunrise\\"];    dataTimingsDhuhr = dataTimings[\\"Dhuhr\\"];    dataTimingsAsr = dataTimings[\\"Asr\\"];    dataTimingsMaghrib = dataTimings[\\"Maghrib\\"];    dataTimingsIsha = dataTimings[\\"Isha\\"];    dataTimingsFajr = \\"0