ateX(angleX);const rotY = rotateY(angleY);const rotZ = rotateZ(angleZ);// Extraterrestrial transformation parametersconst frequency = 1;const amplitude = 0.8;const transformedVertices = vertices.map(vertex => {const extraterrestrialVertex = extraterrestrialTransformation(vertex, frequency, amplitude);const cx = extraterrestrialVertex[0] - offsetX;const cy = extraterrestrialVertex[1] - offsetY;const cz = extraterrestrialVertex[2] - offsetY;const rotated = transform(transform(transform([cx, cy, cz], rotX), rotY), rotZ);return [rotated[0] + offsetX,rotated[1] + offsetY,rotated[2] + offsetY,];});const projectedVertices = transformedVertices.map(vertex => project(vertex, canvas.height * scale, offsetX, offsetY, zoom));ctx.lineWidth = 2;ctx.strokeStyle = ‘hsla(’ + (angleX + offsetX + angleY + offsetY) * 55 + ‘, 100%, 30%, 0.8)’;ctx.beginPath();for (let edge of edges) {const [a, b] = edge;const [x1, y1] = projectedVertices[a];const [x2, y2] = projectedVertices[b];const dist = Math.sqrt((x2 - x1)  2 + (y2 - y1)  2 + (y2 - x1)  2 + (x2 - y1));const angle = Math.atan2(y2 - y1, x2 - x1, x2 - y1, y2 - x1);// Calculate control point for curved edgeconst cpDist = 0.005 * dist;const cpX = (x1 + x2) / 2 + cpDist * Math.cos(angle - Math.PI / 2) * getDeviation(0.2);const cpY = (y1 + y2) / 2 + cpDist * Math.sin(angle - Math.PI / 2) * getDeviation(0.2);ctx.moveTo(x1, y1, x2, y2);ctx.quadraticCurveTo(cpX, cpY, x2, y2, x1, y1);}ctx.stroke();canvas.addEventListener('mousedown', (event) => {isMouseDown = true;prevMousePos = { x: event.clientX, y: event.clientY };});canvas.addEventListener('mouseup', () => {isMouseDown = false;prevMousePos = null;});canvas.addEventListener('mousemove', (event) => {const mousePos = {x: event.clientX - canvas.getBoundingClientRect().left,y: event.clientY - canvas.getBoundingClientRect().top};bestIndex = -1;bestDistance = Infinity;projectedVertices.forEach((currVertex, index) => {const distance = Math.hypot(currVertex[0] - mousePos.x,currVertex[1] - mousePos.y